mapper.select:
		select
		art.*,u.nickname
		from article_t art
        LEFT JOIN user_t u ON u.id = art.user_id
        
            
                AND art.id = #{id}
            
            
                AND art.title = #{title}
            
            
                AND art.content = #{content}
            
            
                AND art.title LIKE CONCAT('%',#{simpleQueryParam},'%')
            
        
        ORDER BY release_time DESC
