mapper.select:
		select
		pre.*,u.nickname
		from reply_t pre
        LEFT JOIN user_t u ON u.id = pre.user_id
        
            
                AND pre.id = #{id}
            
            
                AND pre.article_id = #{articleId}
            
            
                AND pre.user_id = #{userId}
            
            
                AND pre.content LIKE CONCAT('%',#{simpleQueryParam},'%')
            
        
        ORDER BY reply_time DESC
