mapper.select:
		select
		
		from user_t
        
            
                AND id = #{id}
            
            
                AND user_name = #{userName}
            
            
                AND password = #{password}
            
            
                AND age = #{age}
            
            
                AND user_type = #{userType}
            
            
                AND nick_name = #{nickName}
            
            
                AND realname = #{realname}
            
        
        ORDER BY user_type, register_time DESC

select

from user_t
where user_name = #{username} and password = #{password}
